def save_sections_to_markdown(json_data, sections_folder, output_file):
    with open(output_file, "w", encoding="utf-8") as md_file:
        for page, sections in json_data.items():
            md_file.write(f"# {page}\n\n")
            for section_name in sections:
                section_path = os.path.join(
                    sections_folder,
                    # remove _ and number at the end and capitalize the first letter
                    section_name.lower().rsplit("_", 1)[0].capitalize(),
                    f"{section_name.lower()}.html",
                )
                print(section_path)
                if os.path.exists(section_path):
                    with open(section_path, "r", encoding="utf-8") as html_file:
                        html_content = html_file.read()
                        md_file.write(f"## {section_name}\n")
                        md_file.write("```html\n")
                        md_file.write(html_content)
                        md_file.write("\n```\n\n")
                else:
                    md_file.write(f"## {section_name}\n")
                    md_file.write("Section file not found.\n\n")


